mapper.select:
select a.issue_id, a.project_id, a.issue_name, a.issue_detail, a.reg_id, d.state_code, d.state_name from issue a join project b on a.project_id = b.project_id join issuehistory c on a.issue_id = c.issue_id and a.his_date = c.reg_date join state d on c.state_code = d.state_code where a.project_id = #{project_id} and d.type = 'issue'
and c.state_code = #{state_code}
order by a.issue_id

mapper.insert:
insert into issuemember (issue_id, user_id, project_id)
values( 
	#{issue_num},
	#{user_id},
	#{project_num}
)

mapper.update:
update issue
set his_date = #{his_date}
where issue_id = #{issue_id}

update issue
set issue_name = #{issue_name}
, issue_detail = #{issue_detail}
, his_date = #{his_date}
where issue_id = #{issue_id}
