mapper.insert:
	insert into project
		(
			project_name
			, project_start_date
			, project_end_date
			, project_detail
			, manager_id
			, state_code
			, reg_date
		)	
   values
		(
		  #{project_name}
		 ,#{project_start_date}
		 ,#{project_end_date}
		 ,#{project_detail}
		 ,#{manager_id}
		 ,'recruiting'
		 ,sysdate()
		) 

		SELECT LAST_INSERT_ID()

insert into projectdepartment
(    project_id
	, depart_code
	, usercount
)
VALUES
	(
	#{project_id}	
	,#{depart_code}
	,#{usercount}
	)

mapper.select:
select count(*) as cnt
from  projectmember
where project_id = #{project_id}
and user_id != #{user_id}

select state_code  
from  project
where project_id = #{project_id}

mapper.update:
update  project
set project_name =  #{project_name}
	,project_start_date = #{project_start_date}
	,project_end_date =  #{project_end_date}
	,project_detail = #{project_detail}
	,mod_id =  #{mod_id}
	,mod_date  = sysdate()
where project_id = #{project_id}

update projectmember
set
	state_code = #{state_code}	
	,mod_id = #{mod_id}	
	,mod_date  = sysdate()
where project_id = #{project_id}
	 and user_id = #{user_id}
